package Loops;

/*
 * @Author Mohan Pandey
 */
public class PrintNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int counter = 1;
		while (counter <= 100) {
			System.out.println(counter);
			counter = counter + 1;

		}
		System.out.println("Loops Ends! Bye Bye!");
	}
	
}
